function zenang = SZAng(lat,dec,hvec);% function zenang = SZAng(lat,dec,hvec);%	lat    : latitude (rad)%	dec    : solar declination (rad)%	hvec   : hour angle (rad)%	zenang : solar zenith angle (rad)zenang  = acos(sin(lat).*sin(dec) + cos(lat).*cos(dec).*cos(hvec));delz    = 16 * ((zenang-1.5358896)*15.0) / (pi/90);cordel  = ((zenang >= 1.5358896) & (zenang <= pi/2));delz    = cordel .* (delz/60) * (pi/180);zenang  = zenang + delz;