function [SolDir, SolDiff] = SolFlux(Jday,latd, tnoon, time);% function [SolDir, SolDiff] = SolFlux(Jday,latd, tnoon, time);% direct beam and diffuse solar radiation% 	Jday		= Julian date% 	latd		= latitude (¡)% 	tnoon		= time of solar noon in hours + decimal fraction (military)% 	time		= current time in hours + decimal fraction (military)% 	SolDir	= direct beam flux density (W/m2)% 	SolDiff  = diffuse flux density (W/m2)lat = latd*pi/180;     				%convert degrees to radiansdec = SolDec(Jday);					%solar declination (rad)Dlen = DayLength(lat,dec);			%daylength (h)HrAng = SolHrAng(time,tnoon);		%hour angle (rad)SolZen = SZAng(lat,dec,HrAng);	%zenith angle (rad)Solaz = SAzAng(lat,dec,HrAng);SolDir = Qrdir(SolZen,1.013e5,0.01,1);SolDiff = Qdiff(SolZen,1.013e5,0.2,SolDir);