function [maxTa,minTa] = predTa(lat,day,elev);%predict max and min air temp for a given set of conditions%any or all of lat, day, and elev can be a column vector%if more than one is a column vector, they must all have the same length%and each row represents a differnt case.%lat    = latitude (rad)%day    = julian date%elev   = elevation (m)mon     = (12 * day / 365.25) - 6.5;maxcf   = zeros(21,1);  mincf = zeros(21,1);  numests = max([length(lat) length(mon) length(elev)]');factmat = zeros(numests,21);mult    = ones(numests,1);factmat(:, 1) = lat .* mult;factmat(:, 2) = lat.^2 .* mult;factmat(:, 3) = lat.^3 .* mult;factmat(:, 4) = elev .* mult;factmat(:, 5) = elev.^2 .* mult;factmat(:, 6) = mon .* mult;factmat(:, 7) = mon.^2 .* mult;factmat(:, 8) = mon.^3 .* mult;factmat(:, 9) = mon.^4 .* mult;factmat(:,10) = lat .* elev .* mult;factmat(:,11) = mon .* lat .* mult;factmat(:,12) = mon .* elev .* mult;factmat(:,13) = mon .* lat .* elev .* mult;factmat(:,14) = mon.^2 .* lat .* mult;factmat(:,15) = mon.^2 .* elev .* mult;factmat(:,16) = mon.^2 .* lat.^2 .* mult;factmat(:,17) = mon.^2 .* elev.^2 .* mult;factmat(:,18) = mon.^2 .* lat .* elev .* mult;factmat(:,19) = mon.^3 .* lat .* mult;factmat(:,20) = mon.^4 .* lat .* mult;factmat(:,21) = 1.0 .* mult;maxcf( 1) =  1.4082E+1;     mincf( 1) =  1.4991E+1;maxcf( 2) = -2.2700E+1;     mincf( 2) = -2.5196E+1;maxcf( 3) = -5.1641E+0;     mincf( 3) = -6.0622E+0;maxcf( 4) =  2.4483E-4;     mincf( 4) = -4.4556E-3;maxcf( 5) = -1.1871E-6;     mincf( 5) = -4.5246E-8;maxcf( 6) =  4.7644E-1;     mincf( 6) =  2.8271E-1;maxcf( 7) =  5.9008E-2;     mincf( 7) =  3.1415E-2;maxcf( 8) = -1.6372E-2;     mincf( 8) = -8.1083E-3;maxcf( 9) = -8.0143E-4;     mincf( 9) = -7.0519E-4;maxcf(10) = -4.3227E-4;     mincf(10) =  3.4968E-5;maxcf(11) =  2.1268E+0;     mincf(11) =  2.2861E+0;maxcf(12) =  5.2601E-6;     mincf(12) = -4.1092E-5;maxcf(13) = -1.3042E-4;     mincf(13) = -1.5830E-4;maxcf(14) = -1.8214E+0;     mincf(14) = -1.6673E+0;maxcf(15) = -1.1336E-4;     mincf(15) = -3.2511E-5;maxcf(16) = -3.8312E-1;     mincf(16) = -3.3781E-1;maxcf(17) =  3.1188E-8;     mincf(17) =  1.6149E-8;maxcf(18) =  1.4061E-4;     mincf(18) =  1.0097E-4;maxcf(19) = -6.2552E-2;     mincf(19) = -6.5050E-2;maxcf(20) =  2.9604E-2;     mincf(20) =  2.7977E-2;maxcf(21) =  3.2364E+1;     mincf(21) =  2.2410E+1;maxTa = factmat * maxcf;minTa = factmat * mincf;